YUI.add("widgetselecttarget",function(b){function a(){a.superclass.constructor.apply(this,arguments)}a.NAME="WidgetSelectTarget";a.ATTRS={sDestination:{value:""},sSource:{value:""},sSourceDataSet:{value:""}};b.WidgetSelectTarget=b.extend(a,b.WidgetSelect,{initializer:function(){var c=this.get("oWidgetDef");if(!b.Lang.isUndefined(c.options)){if(!b.Lang.isUndefined(c.options.destination)){this.set("sDestination",c.options.destination)}if(!b.Lang.isUndefined(c.options.source)){this.set("sSource",c.options.source)}if(!b.Lang.isUndefined(c.options.sourcedataset)){this.set("sSourceDataSet",c.options.sourcedataset)}}},ValueChangeEvent:function(f){if(this.Field()==null){return}if(this.Field().DataSet().UniqId()==f.DataSet().UniqId()&&this.Field().GetName()==f.GetName()){var h=this.Field().GetValue();var e=this.Layout().GetWidget(this.get("sDestination"));if(h==null){e.Field().SetValue(null)}else{var d=this.Layout().Frame().DataSetManager().DataSet(this.get("sSourceDataSet"));var c=d.PrimaryKey();c.DataSet().RowData().BackupCursor();c.DataSet().RowData().First();while(c.DataSet().RowData().EoF(true)==false){if(c.GetValue()==h){var g=c.DataSet().Field(this.get("sSource"));e.Field().SetValue(g.GetValue());break}c.DataSet().RowData().Next()}}this.Update();e.Update()}}})},"0.0.1",{requires:["widgetfield"]});